      border:"1px solid",
      height:'35px',
      width:'35px',
      display:'none',
      boxShadow: '0px 0px 10px #000000,0px 0px 20px #777777'

    }




  return (

    <>
     <nav className='main-navbar bg-secondary'>
        <div>
        <h1>Portfolio</h1>
        </div>

        <ul className='navbar navbar-collapse' id='navmenu'>
            <li> <Link to='/'>Home</Link> </li>
            <li> <Link to='/Login'>Login</Link>  </li>
            <li> <Link to='/Signup' >SignUp</Link> </li>
            <li> <Link to='/Aboutus' >About </Link> </li>

        </ul>

