      </nav>


      <Routes>
    <Route path='/'  element={ <Landing/> } />
    <Route path='/Login'  element={ <Login/> } />
    <Route path='/Signup'  element={ <Signup/> } />
    <Route path='/Aboutus'  element={ <About/> } />
     </Routes>


    </>
  );
}

export default App;
