import React,{useRef,useState} from 'react'
import Community from './Community'
import { Link , Route, Routes } from 'react-router-dom'
import Axios from 'axios'
import useToggleHook from './useToggleHook'

const Login = () => {
    const [Email, setEmail] = useState('')
    const [Password,setPassword]= useState('')

const data ={
  email:Email,
  password:Password
}

  const{ passToggler,toggle}  = useToggleHook()


  async  function login(e) {
     e.preventDefault()

      let response = await Axios.post('http://localhost:6007/Login',data)

      if(response.data.success){
        localStorage.setItem('token',response.data.token)
      window.location.href = '/Dashboard'
      }else{
       alert(response.data.message)
      }

    }
  return (
    <>
      <form className='login-form' onSubmit={(e)=>login(e)}>
      <h1 style={{margin:'auto'}}>LOG IN</h1>
        <div >
            <label htmlFor="">Email :</label>
            <input onChange={(e)=>setEmail(e.target.value)} value={Email} type="email" className='' required />
        </div>

        <div>
            <label htmlFor=""> Password :</label>
            <input onChange={(e)=>setPassword(e.target.value)} value={Password} ref={passToggler} type="text" className='' required />
        </div>
        <div className='check-field'>
         <label htmlFor="">hide password</label><input  onClick={toggle} type="checkbox" className='' /> 
        </div>

        <div className=''> 
            <button className=' ' >Log in</button> 
        </div>

      </form>
      <Link to='/Forgotten' style={{marginLeft:'40%',marginTop:'30px'}} > forgot password? click here</Link>



    </>
  )
}

export default Login
