import React,{useState,useRef} from 'react'
import Axios from 'axios'



const Signup = () => {
  const [Name, setName] = useState('')
  const [Email, setEmail] = useState('')
  const [Password, setPassword] = useState('')

  const passToggler = useRef()

  const Person = {
    name:Name,
    email:Email,
    password:Password
  }

  const postData = async(e)=>{
    e.preventDefault()
     const theresponse = await Axios.post("http://localhost:6007/getData",Person)

     const result = theresponse.data

   }

  const toggle = ()=>{
    if (passToggler.current.type==="text") {
      passToggler.current.type= "password"
    }else if(passToggler.current.type==="password"){
      passToggler.current.type= "text"
    }
  }
  return (
    <>
      <form className='signup-form'>
      <div>
        <label>NAME :</label><input className='form-control' type="text" value={Name} onChange={(e)=>{ setName(e.target.value)}} />
      </div>


      <div>
        <label >EMAIL :</label><input className='form-control' type="text"  value={Email} onChange={(e)=>{ setEmail(e.target.value)}}/>
      </div>


      <div>
        <label >Password :</label><input ref={passToggler} className='form-control' type="text" value={Password} onChange={(e)=>{ setPassword(e.target.value)}} />
      </div>

     <div className='form-check form-switch '>
     <input onClick={toggle} type="checkbox" className='form-check-input pass-toggler' /> 
     </div>
      <div className='d-grid'>
        <button className='btn bg-dark submit-btn ' style={{color:"white"}} onClick={(e)=>postData(e)}>SUBMIT</button>
       </div>

    </form>
    </>
  )
}

export default Signup
