import React,{useState} from 'react'

const Signup = () => {
  const [Name, setName] = useState('')
  const [Email, setEmail] = useState('')
  const [Password, setPassword] = useState('')

  const Person = {
    name:Name,
    email:Email,

  }

  const postData = async(e)=>{
    e.preventDefault()
     const theresponse = await Axios.post("http://localhost:6007/getData",Person)

     const result = theresponse.data

   }
  return (
    <>
      <form >
      <div>
        <label htmlFor="">NAME</label><input type="text" value={Name} onChange={(e)=>{ setName(e.target.value)}} />
      </div>


      <div>
        <label htmlFor="">EMAIL</label><input type="text"  value={Email} onChange={(e)=>{ setEmail(e.target.value)}}/>
      </div>


      <div>
        <label htmlFor="">ID</label><input type="text" value={Password} onChange={(e)=>{ setPassword(e.target.value)}} />
      </div>

      <div>
        <button onClick={(e)=>postData(e)}>SUBMIT</button>
       </div>

    </form>
    </>
  )
}

export default Signup
