import React,{useRef,useState} from 'react'


const Login = () => {
    const [Email, setEmail] = useState('')
    const [Password,setPassword]= useState('')


    const passToggler = useRef()

    const toggle = ()=>{
        if (passToggler.current.type==="text") {
          passToggler.current.type= "password"
        }else if(passToggler.current.type==="password"){
          passToggler.current.type= "text"
        }
      }

  return (
    <>
      <form className='login-form'>

        <div>
            <label htmlFor="">Email :</label>
            <input onChange={(e)=>setEmail(e.target.value)} value={Email} type="text" className='form-control' />
        </div>

        <div>
            <label htmlFor=""> Password :</label>
            <input onChange={(e)} value={Password} ref={passToggler} type="text" className='form-control' />
        </div>
        <div className='form-check form-switch '>
         <input  onClick={toggle} type="checkbox" className='form-check-input pass-toggler' /> 
        </div>

        <div className='d-grid'> 
            <button className='btn bg-dark ' style={{color:"white"}}>Log in</button> 
        </div>

      </form>
    </>
  )
}

export default Login
