import React,{useRef,useState} from 'react'
import Community from './Community'
import { Link , Route, Routes } from 'react-router-dom'
import Axios from 'axios'


const Login = () => {
    const [Email, setEmail] = useState('')
    const [Password,setPassword]= useState('')

const data ={
  email:Email,
  password:Password
}

    const passToggler = useRef()

    const toggle = ()=>{
        if (passToggler.current.type==="text") {
          passToggler.current.type= "password"
        }else if(passToggler.current.type==="password"){
          passToggler.current.type= "text"
        }
      }
  async  function login() {
      let response = await Axios.post('http://localhost:6007/Login',data)

      console.log('done');
    }
  return (
    <>
      <form className='login-form' onSubmit={login}>

        <div>
            <label htmlFor="">Email :</label>
            <input onChange={(e)=>setEmail(e.target.value)} value={Email} type="text" className='form-control' />
        </div>

        <div>
            <label htmlFor=""> Password :</label>
            <input onChange={(e)=>setPassword(e.target.value)} value={Password} ref={passToggler} type="text" className='form-control' />
        </div>
        <div className='form-check form-switch '>
         <input  onClick={toggle} type="checkbox" className='form-check-input pass-toggler' /> 
        </div>

        <div className='d-grid'> 
            <button className='btn bg-dark submit-btn ' style={{color:"white"}}>Log in</button> 
        </div>

      </form>
      <p style={{marginLeft:'40%',marginTop:'30px'}} > forgot password? click here</p>

      <Community/>

    </>
  )
}

export default Login
