import React,{useState} from 'react'
import Axios from 'axios'
import { Link  } from 'react-router-dom'
import useToggleHook from './useToggleHook'

const Signup = () => {
  const [Name, setName] = useState('')
  const [Email, setEmail] = useState('')
  const [Password, setPassword] = useState('')



  const Person = {
    name:Name,
    email:Email,
    password:Password
  }

const postData = async(e)=>{
    e.preventDefault()
     const theresponse = await Axios.post("http://localhost:6007/Signup",Person)

     const result = theresponse.data
     if (result) {
      window.location.href='/Login'
     } else {
      alert('Error signing up , try again')
      window.location.href = '/Signup'
     }
   }

   const{ passToggler,toggle}  = useToggleHook()


  return (
    <>

      <form className='signup-form'>
      <h1 style={{margin:'auto'}}>SIGN UP</h1>
      <div>
        <label>NAME :</label><input className='' type="text" value={Name} onChange={(e)=>{ setName(e.target.value)}} required />
      </div>


      <div>
        <label >EMAIL :</label><input className='' type="email"  value={Email} onChange={(e)=>{ setEmail(e.target.value)}} required/>
      </div>


      <div>
        <label >Password :</label><input ref={passToggler} className='' type="text" value={Password} onChange={(e)=>{ setPassword(e.target.value)}} required/>
      </div>

     <div className=' '>
     <input onClick={toggle} type="checkbox" className='' /> 
     </div>
      <div className=''>
        <button type='submit' className=''  onSubmit={(e)=>postData(e)}>SUBMIT</button>
       </div>

    </form>


      <div className='have-account'>
        <p> Already have an account? <Link to='/Login'> Login</Link></p>
      </div>
    </>
  )
}

export default Signup
