    setdata(response.data)
   }*/
   const person ={ name:Name,
    id:ID,
    email:Email
  }

    const postData = async(e)=>{
     e.preventDefault()
      const theresponse = await Axios.post("http://localhost:6007/getData",person)

     // const result = theresponse.data

    }




  return (
    <>
      <form >
      <div>
        <label htmlFor="">NAME</label><input type="text" value={Name} onChange={(e)=>{ setName(e.target.value)}} />
      </div>


      <div>
        <label htmlFor="">EMAIL</label><input type="text"  value={Email} onChange={(e)=>{ setEmail(e.target.value)}}/>
      </div>


      <div>
        <label htmlFor="">ID</label><input type="text" value={ID} onChange={(e)=>{ setID(e.target.value)}} />
      </div>

      <div>
        <button onClick={postData}>SUBMIT</button>
       </div>

    </form>
    </>
  );
}

export default App;
